/*    2.	Add code to the testppm program that automatically saves        the image data to a file with a ppm extension.*/// Images#include <stdio.h>#include <math.h>#include <stdlib.h>#include <time.h>int main () {    srand((unsigned)time(NULL));    int height = 600;    int width = 600;    int col = 0;    int i,j;    FILE *fout;    fout = fopen("5-2.ppm","w");    fprintf(fout,"P3\n%d %d\n255\n", width, height);    for(i=1;i<=width;i++){        for(j=1;j<=height;j++){            col = (int)fabs(255.0*sin(i/11.0)*floor(cos(j/10.0)));            fprintf(fout,"%d\n%d\n%d\n", col,rand()%256,rand()%256);        }    }    fclose(fout);}