/*    1.	Add code to the Mandelbrot program that writes the data to a file with ppm extension.*/// The Mandelbrot set#include <stdio.h>#include <math.h>#include <complex.h>#include <stdlib.h>#define ncols 800#define nrows 800int iterate_pt(double complex c);int Mand[ncols][nrows];int main() {    FILE *f1=fopen("6-1.ppm","w");    fprintf(f1,"P3\n%d %d\n255\n",ncols,nrows);    double crmin = -0.75;    double crmax = -0.74;    double cimin = -0.138;    double cimax = -0.127;    int x, y;    int color;    double complex c;    double complex imag = 0.0 + 1.0i;    double dx = (crmax - crmin)/ncols;    double dy = (cimax - cimin)/nrows;    for(y = 0; y < nrows; y++){        for(x = 0; x < ncols; x++){            c = (crmin + x*dx) + (cimin + y*dy)*imag;            Mand[x][y] = iterate_pt(c) + 65;        }    }    for(y = 0; y < nrows; y++){        for(x = 0; x < ncols; x++){            color = Mand[x][y];            fprintf(f1,"%f\n%f\n%f\n", pow(color,1.4),pow(color,1.9),pow(color,2));        }    }    fclose(f1);    system("open -a /Applications/GIMP.app 6-1.ppm");    return 0;}int iterate_pt(double complex c) {    double complex z = 0.0 + 0.0i;    int iterations = 0;    int k;    for (k = 1; k <= (255-64); k++){        z = z*z + c;        if(sqrt(z*conj(z)) > 50){            break;        } else {            ++iterations;        }    }    return iterations;}